	/**
	 * <p> Returns a array of random colors by given size</p>
	 *
	 * @param size
	 *            a positive integer.
	 *
	 * @return int[]
	 */
	public static int[] getRandomMaterialColor(int size){
		int[] colors = new int[size];
		for (int i=0; i<size; i++){
			colors[i]= getRandomMaterialColor();
		}

		return colors;
	}

